const merged = {};

for (const item of items) {
  const urn = item.json["Company URN"];
  if (!urn) continue;

  if (!merged[urn]) {
    merged[urn] = {};
  }

  for (const [key, value] of Object.entries(item.json)) {
    if (
      value !== undefined &&
      value !== null &&
      value !== "" &&
      merged[urn][key] === undefined
    ) {
      merged[urn][key] = value;
    }
  }
}

return Object.values(merged).map(data => ({ json: data }));

const leads = items.map(item => {
  const data = item.json;
  return {
    "Name": data.Name || "",
    "Company Name": data["Current company"] || data["Company Name"] || "",
    "Position": data.Position || "",
    "Headline": data.Headline || "",
    "Location": data.Location || "",
    "Lead Score": data["Lead Score"] || "",
    "Profile URL": data["Profile URL"] || "",
    "Company URL": data["Company URL"] || "",
    "Industry": data.Industry || ""
  };
});

return [{ json: leads }];

const item = items[0].json;

// basic signals (safe defaults)
const hasContactPage =
  (item.text || "").toLowerCase().includes("contact");

const hasFAQ =
  (item.text || "").toLowerCase().includes("faq");

const pagesCovered = [
  item.url || item.loadedUrl || "unknown"
];

const rawEvidence = [
  (item.text || "").substring(0, 400)
];

const serviceComplexity =
  (item.text || "").length > 1500 ? "HIGH" : "MEDIUM";

return [{
  json: {
    website_signals: {
      hasContactPage,
      hasFAQ,
      serviceComplexity,
      pagesCovered,
      rawEvidence
    }
  }
}];

const aiText = items[0].json.output || '';

// --------------------
// 1. AI FIT CLASSIFICATION
// --------------------
let aiFit = 'UNKNOWN';

if (aiText.includes('PITCH_WORTHY')) {
  aiFit = 'PITCH_WORTHY';
} else if (aiText.includes('NOT_PITCH_WORTHY')) {
  aiFit = 'NOT_PITCH_WORTHY';
}

// --------------------
// 2. RECOMMENDED SOLUTIONS EXTRACTION
// --------------------
const recommendedSolutions = aiText
  .split('\n')
  .filter(line =>
    line.toLowerCase().includes('chatbot') ||
    line.toLowerCase().includes('automation') ||
    line.toLowerCase().includes('faq')
  )
  .map(line =>
    line
      .replace(/\*\*/g, '')
      .replace(/^\d+\.\s*/, '')
      .trim()
  );

// --------------------
// 3. CLEAN PITCH REASONING
// --------------------
let cleanReasoning = aiText;

// remove markdown
cleanReasoning = cleanReasoning.replace(/\*\*/g, '');

// remove excessive newlines
cleanReasoning = cleanReasoning.replace(/\n+/g, ' ');

// shorten for CRM / sales use
cleanReasoning = cleanReasoning.slice(0, 400).trim();

// --------------------
// FINAL OUTPUT
// --------------------
return [{
  json: {
    ai_fit: aiFit,
    recommended_solutions: recommendedSolutions,
    pitch_reasoning: cleanReasoning
  }
}];

const s = items[0].json.website_signals;

const context = `
Website Analysis Summary:

- Contact page present: ${s.hasContactPage ? "Yes" : "No"}
- FAQ or Support section present: ${s.hasFAQ ? "Yes" : "No"}
- Service complexity level: ${s.serviceComplexity}

Pages analyzed:
${s.pagesCovered.join(", ")}

Sample website content:
${s.rawEvidence.join("\n---\n")}
`;

return [{
  json: {
    ai_context: context
  }
}];
